INSERT INTO prog VALUES (10,10,4,103,37,2,NULL,NULL,NULL,'Atlas Scientifique du Mont-Blanc','Modélisation de la distribution future de certaines espèces','Modelling the future niche distribution of flora specie','Cartographie de la répartition future d''espèces sur le massif du Mont-Blanc.\r\nLes cartographies sont obtenues à partir de modélisations qui intègrent des informations sur la biologie des espèces ainsi que des modélisations du climat futur.','Mapping the future niche distribution of flora specie on the Mont-Blanc.\r\nThe maps are obtained from models that incorporate information about the species biology and some models that  simulate future climate change.','Université de Bale, WSL, LECA, CREA',';;ecolo;climato','flore;climat;faune;','2012-01-01',NULL,'cartes;donnees;modelisation;analyse;','cartes;donnees;modelisation;analyse;','','','http://www.atlasmontblanc.org','','2013-01-08');
