INSERT INTO prog VALUES (15,14,11,NULL,NULL,NULL,NULL,NULL,NULL,'ZNIEFF','L’inventaire des Zones Naturelles d’Intérêt Ecologique Faunistique et Floristique (ZNIEFF)','','Une ZNIEFF (Zones Naturelles d’Intérêt Ecologique Faunistique et Floristique) est “un secteur du territoire national pour lequel les experts scientifiques ont identifié des éléments remarquables du patrimoine naturel”. L''inventaire des ZNIEFF est donc un outil de connaissance fondamental dans le domaine de la biodiversité.\r\n\r\nCet inventaire, traitant de l''ensemble du domaine du vivant, constitue aujourd''hui l''un des éléments majeurs de la politique de protection de la nature, et la clef de voûte de l''Inventaire National du Patrimoine Naturel (INPN). Il a pour objectif d’identifier et de décrire des secteurs présentant de fortes capacités biologiques et un bon état de conservation.','','France : DREAL ; Le Ministère de l''Ecologie ; MNHN',';geol;ecolo;','flore;faune;sol;','1982-01-01',NULL,'cartes;donnees;','cartes;donnees;','','','http://www.rdbrmc-travaux.com/spge/site_v2/article_znieff_produits.php?dep=74&nom_dep=Haute-Savoie','','2013-01-03');
