INSERT INTO prog VALUES (19,88,61,NULL,NULL,NULL,NULL,NULL,NULL,'','Botanique des zones d''Alpages et étude de populations d''Edelweiss sur le Massif du Mont-Blanc','Botanical survey of high mountain pastures and Edelweiss population survey','Dans un contexte ou les diverses activités humaines ont contribué à la raréfaction de nombreuses espèces de plantes et ceci, même dans les régions alpines. L''étude cherche a étudier les dynamiques de recolonisation des pelouses et pâturages par l''Edelweiss. L''étude permet également d''observer les cycles de floraison, estimer des paramètres de la reproduction végétative','','Association Elyna',';;ecolo;','flore;','2005-01-01',NULL,'donnees;analyse;','donnees;analyse;','','','http://www.espace-nature.ch/','','2012-12-12');
INSERT INTO prog VALUES (20,20,42,NULL,NULL,NULL,NULL,NULL,NULL,'Tré la tête','Suivi du glacier de Tré la tête','','Suivre l’évolution des glaciers, observer l’impact du changement climatique en étudiant le glacier de Tré la Tête, situé au cœur de la Réserve Naturelle des Contamines-Montjoie.\r\n\r\nCe glacier dispose d''une importante série d''observations de recul du front du glacier. La première date 1730 (Mappe Sarde). Les mesures ont été faites successivement par différents organismes. Depuis 1999, Asters a pris la relève de cette série et mesure la distance du front à un point fixe. Le glacier a reculé de 2,5 km en 270 ans.\r\n\r\nSur chaque site, il s’agit de prendre, chaque année, des photos à focale fixe chaque année des trois glaciers, selon des points définis dans le protocole, afin de comparer l’évolution de forme du glacier, son recul et d’en faire une analyse qualitative. Une mesure est également faite depuis un point fixe jusqu’au front du glacier pour évaluer le recul du front.','','Aster,  L. Moreau, S. Coutterand, CNM (Centre de la Nature Montagnarde)','glacio;;;','glace;','1999-01-01',NULL,'cartes;donnees;analyse;','cartes;donnees;analyse;','','','','','2013-01-07');
