INSERT INTO prog VALUES (31,93,21,30,55,NULL,NULL,NULL,NULL,'Catasto Ghiacciai - Valle d''Aosta','Cadastre des glaciers de la Région Autonome du Val d''Aoste','','La Base de données est dédiée aux Glaciers Valdotains. \r\nElle regroupe des informations  sur l''extension actuelle et historique des glaciers, des glaciers rocheux et des lacs glaciaires. On trouve également des informations sur les signalements des événements liés à l''environnement glaciaire; des données spécifiques pour certains types de glaciers et une section de photographies.','','Fondazione Montagna sicura ; l''ARPA VdA ; Regione Autonoma Valle d''Aosta','glacio;;;','risque;geomorpho;eau;glace;','2005-01-01',NULL,'cartes;donnees;analyse;','cartes;donnees;analyse;','','','http://catastoghiacciai.regione.vda.it/ghiacciai/mainghiacciai.html','','2012-12-13');
