INSERT INTO prog VALUES (33,59,2,80,28,NULL,NULL,NULL,NULL,'PhenoALP','Etude des variations phénologiques causées par le changement climatique dans les Alpes','Study of phenological changes in the Alps','PhenoAlp a pour objectifs le développement d''un réseau d''observation, la création de protocoles communs de suivi de la phénologie à long terme et la sensibilisation du grand public aux enjeux du changement climatique','PhenoAlp aims to develop a monitoring network, perform common protocols for monitoring long-term phenology and public awareness to the challenges of climate change','ARPA Valle d''Aosta Agenzia Regionale per la Protezione dell''Ambiente, CREA Centre de Recherches sur les Ecosystèmes d’Altitude, Parco Naturale Mont Avic, Parc Naturel Regional du Massif des Bauges, Assesorato Agricoltura e Risorse Naturali Regione Autonoma Valle d''Aosta',';;ecolo;climato','flore;climat;faune;air;','2009-01-26','2012-03-27','cartes;donnees;analyse;','cartes;donnees;analyse;','','','http://en.phenoalp.eu/index.php?lang=en','','2012-09-13');
