INSERT INTO prog VALUES (56,65,28,NULL,NULL,NULL,NULL,NULL,NULL,'','Inventaire forestier National','','Relevés et cratérisation du peuplement forestier des étendues boisées Italiennes\r\n\r\nLes relevés sont effectués par le Corps Forestier de la Vallée d´Aoste, en fonction d''une méthodologie nationale.\r\nDans un premier temps la présence de zones boisées est déterminée grâce à l''interprétation de photographies aériennes. Une fois les zones déterminées, un tiers des placettes sont sélectionnées et caractérisées par des relevés généraux. \r\nUn tiers de ces placettes sont également sélectionnées pour être caractérisées de façon plus approfondie.','','RAVA, Corps Forestier de l''État',';;ecolo;','flore;',NULL,NULL,'cartes;donnees;analyse;','cartes;donnees;analyse;','','','http://www.regione.vda.it/risorsenaturali/corpoforestale/default_f.asp','','2012-12-13');
INSERT INTO prog VALUES (57,66,28,NULL,NULL,NULL,NULL,NULL,NULL,'PRFV - Piano Regionale Faunistico-Venatorio','Plan régional Faune et Chasse','','Le ''Piano Regionale Faunistico-Venatorio'' est un plan d''action quinquennal ayant pour but la gestion de la faune sauvage et la réglementation de la chasse\r\n\r\nCe plan renseigne sur l''état des populations de la faune sur le territoire de la vallée d''Aoste.  Selon les espèces, le plan récence diverses informations (nombre d''individus par population, nombre d''individus chassées ...).\r\n\r\nLes espèces étudiées sont :\r\nles chamois : Rupicapra rupicapra, les bouquetins : Capra ibex; les cerfs élaphe : Cervus elaphus; les chevreuils : Capreolus capreolus; les sangliers : Sus scrofa;\r\nles lièvres  : Lepus europaeus, Lepus timidus;  \r\nle Tetras Lyre : Tetrao tetrix; le lagopède Alpin : Lagopus mutus helveticus; Perdrix : Alectorix graeca saxatilis et autres espèces d''avifaune chassable.\r\nle renard roux : Vulpes vulpes, le loup Canis lupus\r\nla marmotte : Marmota marmota','','RAVA',';;ecolo;','faune;',NULL,NULL,'cartes;donnees;analyse;','cartes;donnees;analyse;','','','http://www.regione.vda.it/risorsenaturali/risorsenaturali/pianofaunistico_f.asp','','2012-12-13');
