INSERT INTO prog VALUES (64,73,13,42,12,NULL,NULL,NULL,NULL,'Galliformes de Montagne','Comptages des galliformes de montagne sur le Massif du Mont Blanc','','Évaluation de l''abondance, la tendance et le succès de la reproduction des populations de galliformes de montagne : Tétras-lyre, Perdrix bartavelle, Lagopède alpin \r\n\r\nCertaines études ponctuelles ont également été menées sur le Tetras-Lyre. Le lagopède alpin fait également l''objet d''un suivi télémétrique par le biais de l''équipement de certaines poules.','','OGM (Observatoire des Galliformes de Montagne), Aster',';;ecolo;','faune;','2000-04-01',NULL,'donnees;analyse;outils;','donnees;analyse;outils;','Ces comptages sont coordonnés par l''observatoire des galliformes de montagne (OGM) qui réalisent des bilans démographiques annuels (tendance des effectifs, cartographie) sur des aires biogéographiques déterminées comme celle des Préalpes du Nord.','','','','2013-01-07');
