INSERT INTO prog VALUES (103,108,4,2,NULL,NULL,NULL,NULL,NULL,'NDVI','Analyse de l''indice NDVI sur le massif du Mont-Blanc','','L''analyse de l''imagerie MODIS (MOD13Q1), permet de calculer un indice NDVI (Normalized Difference Vegetation Index  - indice de végétation normalisé).\r\nCet indice de végétation permet de voir la couverture végétale sur les images de télédétection. L’évolution de cet indice au cours des mois permet d''observer les cycles de végétation à l''échelle d''un massif.','','LECA, CREA',';;ecolo;','flore;','2000-01-01',NULL,'cartes;donnees;','cartes;donnees;','','','','','2012-12-17');
INSERT INTO prog VALUES (104,105,5,NULL,NULL,NULL,NULL,NULL,NULL,'','Étude géomophologique des flux glaciaires dans les Alpes nord-occidentales','Geomorphologic study of ice flows in north-western Alps','Reconstitution des flux glaciaires des Alpes nord-occidentales pendant le maximum d''englacement würmien. \r\n- Acquisition de nouvelles observations géomorphologiques permettant la réalisation d''une cartographie paléogéographique cohérente du système glaciaire au maximum würmien\r\n- Détermination de l''origine des différents flux glaciaires ayant alimenté les lobes de piémont des Alpes nord-occidentales. Par des approche glacio-morphologique, des analyses pétrographiques des blocs erratiques  et des analyses des minéraux lourds contenus dans la fraction fine des tills d''ablation.','-','','glacio;;;climato','climat;geomorpho;glace;',NULL,NULL,'cartes;donnees;analyse;','cartes;donnees;analyse;','','','http://www.glaciers-climat.com/accueil.html','','2013-01-07');
INSERT INTO prog VALUES (105,116,22,80,NULL,NULL,NULL,NULL,NULL,'Transalp''AIR','Indice de la qualité de l''air en direct sur le Massif du Mont-Blanc','','Stations de surveillance de la qualité de l''aire et création d''un indice de la qualité de l''air déterminé à partir des concentrations de quatre polluants : le dioxyde soufre (SO2), le dioxyde d''azote (NO2), l''ozone (O3) et les poussières (PM10).','','Air-APS (L''Air de l''Ain et des Pays de Savoie), SEVEN (Service de l''Environnement et de l''Energie du canton de Vaud),  SPAir (Service de Protection de l''Air), POPAG (Réseau d''Observation de la Pollution Atmosphérique a Geneve), ARPA (Agence Régionale pour la Protection de l''Environnement -\r\nSection Air)',';;;climato','climat;air;','1995-01-01',NULL,'donnees;analyse;','donnees;analyse;','','','http://www.transalpair.eu/partenaire_airaps','','2012-11-15');
