INSERT INTO prog VALUES (136,155,115,NULL,NULL,NULL,NULL,NULL,'','Pergelisol Alpin','Pergelisol Alpin','Alpine permafrost','Suivi du glacier rocheux du Dérochoir: mesures de température du sol, mesures de déplacement. Ce glacier rocheux alimente en débris le torrent des Arandellys et présente donc un enjeu en termes de risques naturels. Une meilleure connaissance de sa dynamique permettra de mieux évaluer l''aléa et son évolution.','','','glacio;geol;;','risque1;geomorpho;geomorpho1;glace;',NULL,NULL,'cartes;donnees;analyse;','cartes;donnees;analyse;','','','','','2012-09-13');
