INSERT INTO prog VALUES (168,NULL,103,37,2,NULL,NULL,NULL,NULL,'Atlas Scientifique du Mont-Blanc','Modélisation des conditions climatiques du massif du Mont-Blanc','','Modélisation des conditions climatiques sur le massif du Mont Blanc entre 1990 et 2100 :\r\nLes modélisations donnent des cartographies de la températures et des précipitations envisagées pour les années 2005 / 2035 / 2065 et 2085. Ces modélisations se basent sur le scénario climatiques A1B du GIEC.','','Université de Bâle, WSL, CREA',';;;climato','climat;','2012-01-01',NULL,'donnees;modelisation;analyse;','donnees;modelisation;analyse;','','','','','2013-01-21');
