INSERT INTO prog VALUES (180,NULL,121,NULL,NULL,NULL,NULL,NULL,NULL,'','Climat et glacier du versant Italien du Mont-Blanc','','Ces suivis sur le long terme permettent d''étudier la relation entre les changements climatique et les variations de tailel des glaciers dans les régions du Val Ferret et du Val Veny.\r\n\r\nLes  glaciers du Val Ferret et du Val Veny sont suivi à l''aide de photographies répétées. \r\nSur les glaciers de la Lex Blanche, la Brenva, Toula et Pré-de-bard, des suivis de la variation des fronts du glacier sont également mises en place.','','Comitato Glaciologico Italiano','glacio;;;climato','climat;glace;','1961-01-01',NULL,'donnees;analyse;','donnees;analyse;','','','','','2012-11-14');
