INSERT INTO prog VALUES (213,125,11,NULL,NULL,NULL,NULL,NULL,NULL,'Banque Hydro','Hydrologie en Rhône-Alpes','','Des station de mesure hydrométrique sont disposées sur certains cours d''eau. Elles permettent d''obtenir des informations concernant les débits des cours d''eau. (Conditions du moment et bulletins bimensuels concernant la ressource en eau)\r\n\r\nLes informations enregistrées par les stations sont utilisées par le service de prévision des crues.','','DREAL Rhône-Alpes Service Prévention des Risques','glacio;;;','eau;','1936-01-01',NULL,'donnees;','donnees;','','','http://www.hydro.eaufrance.fr/presentation/procedure.php','','2012-12-04');
