INSERT INTO prog VALUES (215,NULL,126,NULL,NULL,NULL,NULL,NULL,NULL,'RCS','Réseau de contrôle de surveillance des eaux','','Réseau de mesure mis en place dans le cadre du programme de surveillance de la La Directive-Cadre sur l’Eau.  Il permet d’évaluer l’état général des eaux et les tendances d’évolution au niveau d’un bassin. Il est constitué de stations de mesures représentatives du fonctionnement global de la masse d’eau.','','','glacio;;;','eau;',NULL,NULL,'donnees;','donnees;','','','http://www.rhone-mediterranee.eaufrance.fr/','','2012-12-03');
INSERT INTO prog VALUES (216,NULL,126,NULL,NULL,NULL,NULL,NULL,NULL,'RCO','Réseau de contrôle opérationnel - qualité des eaux','','Réseau de mesure mis en place dans le cadre du programme de surveillance de la La Directive-Cadre sur l’Eau. \r\n Il a pour objectifs de :\r\n- d’assurer le suivi de toutes les masses d’eau qui ne pourront pas atteindre le bon état en 2015 (masses d’eau ayant obtenu un report ou une dérogation d’objectif de bon état pour 2021 ou 2027),\r\n- d’assurer le suivi des améliorations des eaux, suite aux actions mises en place dans le cadre des programmes de mesures,\r\n- et le cas échéant de préciser les raisons de la dégradation des eaux.','','','glacio;;;','eau;',NULL,NULL,'donnees;analyse;','donnees;analyse;','','','','','2012-12-03');
INSERT INTO prog VALUES (217,NULL,126,NULL,NULL,NULL,NULL,NULL,NULL,'Qualité des eaux','Stations de mesure de qualité des eaux','','L''ensemble des études menées sur les cours d''eau sont recensées dans une base de donnée : le Système d''information sur l''eau de bassin Rhône Méditerranée.\r\nLes données concernant les eaux superficielles  sont disponible a l''adresse ci dessous :\r\nhttp://sierm.eaurmc.fr/eaux-superficielles/index.php\r\nLes données concernant les eaux souterraines  sont disponible a l''adresse ci dessous :\r\nhttp://sierm.eaurmc.fr/eaux-souterraines/index.php\r\n\r\nCette base recense l''ensemble des réseau locaux, régionaux et nationaux de surveillance de la qualité des cours d''eau et eaux superficielles. Tant pour les suivis que concernant les études ponctuelles.\r\n\r\nLes mesures peuvent être diverses :\r\n- p.H., température, oxygène dissous, matières organiques (D.B.O.5, D.C.O., oxydabilité et carbone organique), minéralisation et salinité (conductivité, chlorures, sulfates, calcium, magnésium, potassium, sodium, bicarbonates, T.A.C., dureté), composés de l''azote (nitrates, nitrites, ammonium) et du phosphore (phosphates).\r\n- indicateurs de contamination fécale (coliformes, streptocoques).\r\n- chlorophylle a, phéopigments.\r\n-  I.B.G.N. (invertébrés), I.B.D. (diatomées).\r\n- arsenic, cadmium, chrome, cuivre, mercure, nickel, plomb, zinc.\r\n- composés du type solvants chlorés (chloroforme, trichloréthylène, etc.), hydrocarbures aromatiques polycycliques (benzo(a)pyrène, etc.), benzène, toluène, xylènes, polychlorobiphényles (P.C.B.), chlorobenzènes, chlorophénols, etc.\r\n- pesticides (herbicides, insecticides, fongicides, etc.)','','','glacio;;;','eau;','1970-01-01',NULL,'donnees;analyse;','donnees;analyse;','','','http://sierm.eaurmc.fr/eaux-superficielles/index.php','','2012-12-05');
