INSERT INTO prog VALUES (220,76,42,NULL,NULL,NULL,NULL,NULL,NULL,'','Inventaires départementaux des zones humides','','Identification et cartographie des zones humides; les données de terrain éventuellement recueillies sont également disponibles.  \r\n\r\nL’inventaire départemental des zones humides est un outil d’alerte qui sert à prévenir de la présence de zones humides, sans zonage précis. La délimitation des sites doit donc être affinée en cas de besoin. Au même titre que l''inventaire ZNIEFF, cet inventaire n''est pas réglementaire et ne constituent pas directement des zonages opposables.\r\n\r\nCet inventaire n''est pas exhaustif mais tend à l''être au fur et à mesure des compléments. Sur le territoire de l''Atlas Scientifique du Mont-Blanc, une actualisation de certaines données va être mise en place prochainement.','','ASTER, CPNS','glacio;;ecolo;','flore;faune;eau;','1995-01-01',NULL,'cartes;donnees;','cartes;donnees;','','','http://www.zoneshumides-rhonealpes.fr/','','2013-01-22');
INSERT INTO prog VALUES (221,NULL,11,NULL,NULL,NULL,NULL,NULL,NULL,'','Inventaire du Patrimoine Géologique de la Région Rhône-Alpes','','L’inventaire du patrimoine géologique est un outil de connaissance, d’alerte et non de protection. Une fois mis à disposition des utilisateurs potentiels, il devient un élément clé pour une meilleure prise en compte des richesses géologiques dans l’aménagement du territoire, y compris dans la valorisation des territoires et la préservation de leur patrimoine.','','DREAL RA','glacio;geol;;','geomorpho;geomorpho1;tectonique;roche;sol;','2007-01-01',NULL,'cartes;donnees;analyse;','cartes;donnees;analyse;','fiches descriptives des sites à venir','','','','2013-01-15');
INSERT INTO prog VALUES (222,NULL,13,NULL,NULL,NULL,NULL,NULL,NULL,'Tértas-lyre','Pland d''action en faveur du Tértas-lyre','','Le Tétras-lyre est une espèce à forte valeur patrimoniale, témoin de la présence d’habitats très riches au plan biologique. En France, son état de conservation peut être considéré comme DEFAVORABLE INADEQUAT d’après la typologie établie par le Muséum d’Histoire Naturelle. \r\n\r\nLes Alpes du Nord abritent près des 2/3 des effectifs français. Cette forte responsabilité a conduit la DREAL  et le CG Rhône-Alpes à initier en 2009 un plan d’actions régional en faveur de la conservation du tétras-lyre et de ses habitats. Cette démarche repose sur un large partenariat pour pouvoir agir simultanément sur les différents facteurs','','DREAL Rhône Alpes, Région Rhône-Alpes, ONCFS, OGM',';;ecolo;','faune;','2009-01-01',NULL,'donnees;analyse;outils;','donnees;analyse;outils;','','','','','2012-12-03');
