INSERT INTO prog VALUES (224,NULL,11,NULL,NULL,NULL,NULL,NULL,NULL,'','Inventaire régional des tourbières de Rhône-Alpes','','Cartographie des Tourbières de Rhône-Alpes.\r\n\r\nPour chaque tourbière recensées, production de \r\n- La cartographie du site\r\n- La cartographie du bassin versant\r\n- Une fiche descriptive du site.\r\n\r\nLes cartographies permettent de créer deux table géographiques (sites et bassins versants) produites par numérisation d''objets sur fonds de cartes numériques IGN (scan 25).','','Inventaire financé par l''Etat, la Région Rhône-Alpes, l''Agence de l''Eau et les Départements de la Drôme, de l''Isère et de la Savoie, coordonné et réalisé par le Conservatoire Rhône-Alpes des espaces naturels en 1999.',';;ecolo;','flore;faune;','1999-01-01',NULL,'cartes;donnees;analyse;','cartes;donnees;analyse;','','','','','2012-12-04');
INSERT INTO prog VALUES (225,NULL,11,NULL,NULL,NULL,NULL,NULL,NULL,'','Observatoire des Paysages','','Cet observatoire des paysages identifie les paysages rhonalpins  (301 unités paysagères), les regroupe selon des caractéristiques communes en 7 familles. L''observatoire permet également de suivre leurs évolutions, pour proposer une prise en compte dans les politiques publiques et mettre en place des moyens d’action.\r\n\r\nL''ensemble des unités paysagères recensées ont étés sujettes à des visites de terrain et des photo-interprétations. Pour chaque unité, un parcours photographique illustre l’itinéraire réalisé lors de la visite de terrain.','','DREAL Service Aménagement Paysages Infrastructures',';;ecolo;','flore;','1996-01-01',NULL,'cartes;donnees;outils;','cartes;donnees;outils;','','','http://www.rdbrmc-travaux.com/spge/site_v2/z_new_graphique.php?dreal=orp.php','','2012-12-19');
