INSERT INTO prog VALUES (228,NULL,130,NULL,NULL,NULL,NULL,NULL,NULL,'','Différentes études hydrauliques et des transports solides sur l''Arve et ses affluents torrentiels','','Etudes des risques naturels liés aux débordements torrentiels et aux apports de matériaux solides de nature à engraver les fonds des lits et à aggraver les risques de débordement. Permet d''identifier les secteurs à enjeux exposés aux risques torrentiels, et de proposer/réaliser des ouvrages de protection.','','','glacio;;;','risque;geomorpho;eau;',NULL,NULL,'cartes;modelisation;analyse;outils;','cartes;modelisation;analyse;outils;','','','','','2012-12-07');
INSERT INTO prog VALUES (229,NULL,130,NULL,NULL,NULL,NULL,NULL,NULL,'Etude qualité','Points d''évaluation de la qualité des eaux de l''Arve','','Afin de contrôler l''évolution de la qualité des eaux de l''Arve, plusieurs points de contrôle on été définis. Des campagnes de mesure sont réalisés tous les 2 ans en moyenne (paramètres physico-chimiques, métaux, IBGN)','','','glacio;;;','eau;',NULL,NULL,'donnees;analyse;','donnees;analyse;','','','','','2012-12-07');
INSERT INTO prog VALUES (230,NULL,10,NULL,NULL,NULL,NULL,NULL,NULL,'','l''inventaire statistique','','Il s''agit d''une inventaire relascopique par échantillonnage systématique sur une grille de pas 100 m dans la direction NE-SO (approximativement le sens des courbes de niveau) et 150 m dans la direction NO-SE. Au total, 1500 placettes ont ete inventoriées, dont 1173 avec une valeur de surface terrière totale non nulle\r\n\r\nVallée de Chamonix','','ONF',';;;','',NULL,NULL,'','','','','','','2012-12-10');
INSERT INTO prog VALUES (231,NULL,132,NULL,NULL,NULL,NULL,NULL,NULL,'','Histoire structurale du massif du Mont-Blanc et des Aiguilles Rouges','Alpine thermal and structural evolution of the highest external crystalline massif: The Mont Blanc','Cartographie des structures géologiques des massifs du Mont-Blanc et des Aiguilles-Rouges et études géochronologiques afin de retracer l''évolution structurale du Massif depuis 50 Ma','Mapping of the structural units of the Mont-Blanc and Aiguilles Rouges Ranges and geochronological study in order to decipher the structural history of the range during the alpine collision.','IPGP, CNRS, INSU, BRGM, Université Lyon1',';geol;;','tectonique;','2000-03-01','2007-09-01','cartes;donnees;modelisation;analyse;','cartes;donnees;modelisation;analyse;','Alpine thermal and structural evolution of the highest external crystalline massif: The Mont Blanc, P. H. Leloup, N. Arnaud, E. R. Sobel, and R. Lacassin, TECTONICS, VOL. 24, TC4002, doi:10.1029/2004TC001676, 2005\r\nReply to comment by Y. Rolland et al. on ‘‘Alpine thermal and structural evolution of\r\nthe highest external crystalline massif: The Mont Blanc,’’, Leloup, P. H., N. Arnaud, R. Lacassin, and E. R. Sobel (2007), Tectonics, 26, TC2016, doi:10.1029/2006TC002022.','Alpine thermal and structural evolution of the highest external crystalline massif: The Mont Blanc, P. H. Leloup, N. Arnaud, E. R. Sobel, and R. Lacassin, TECTONICS, VOL. 24, TC4002, doi:10.1029/2004TC001676, 2005\r\nReply to comment by Y. Rolland et al. on ‘‘Alpine thermal and structural evolution of\r\nthe highest external crystalline massif: The Mont Blanc,’’, Leloup, P. H., N. Arnaud, R. Lacassin, and E. R. Sobel (2007), Tectonics, 26, TC2016, doi:10.1029/2006TC002022.','http://www.insu.cnrs.fr/terre-solide/lithosphere-continentale/pourquoi-et-depuis-quand-le-mont-blanc-est-il-si-haut;http://geologie.ens-lyon.fr/HERVE/recherche/Mt-Blanc/MB1.html;','','2012-12-17');
INSERT INTO prog VALUES (232,NULL,94,NULL,NULL,NULL,NULL,NULL,NULL,'','Suivi de l''évolution temporelle de la conductivité thermique effective de la neige','Monitoring of the time evolution of the effective thermal conductivity of snow','La conductivité thermique effective de neige etait automatiquement mesuré à l''aide de sondes incorporées dans la couche de neige, au cours d''une période de temps de 3 mois.\r\nL''expérience a été réalisée du 15 Janvier au 8 Avril 2009, sur la moraine de la rive droite du glacier d''Argentière, à une altitude d''environ 2400 m. Les mesures de keff (conductivité thermique effective) ont été réalisées de façon automatique une fois tous les 2 jours à 5:00 heures UTC. Les mesures ont été effectuées tôt le matin pour s''assurer que les conditions plus froides possibles dans la partie supérieure du manteau neigeux.','The effective thermal conductivity of snow was automatically measured using heated-needle probes embedded in the snowpack over a time period of 3 months.\r\nThe field experiment was carried out from 15 January to 8 April 2009, on the right bank moraine of the Argentière Glacier at an altitude of ca. 2400 m. Measurements of keff were automatically performed once every 2 days at 5:00 UTC. Measurements were performed in the early morning to ensure the coldest possible conditions in the uppermost snowpack.','GAME, CEN (Météo-France), LGGE','glacio;;;','neige;','2009-01-15','2009-04-08','donnees;analyse;','donnees;analyse;','','','','','2013-01-07');
INSERT INTO prog VALUES (233,NULL,94,NULL,NULL,NULL,NULL,NULL,NULL,'','Analyse et prévision des avalanches liées à des conditions d''enneigement exceptionelles','Analysis and forecast of extreme new-snow avalanches','','Snow and weather conditions typical of exceptional cycles of fresh-snow avalanches in the northern Alps were investigated using the numerical avalanche-hazard forecasting procedure of Météo-France. Sensitivity tests were performed on the events of February 1999 in the Chamonix France region, and resulting snowpack instability modeled at the massif scale were compared using adapted new indices and maps.','GAME/CNRM (Météo-France)','glacio;;;climato','risque;meteo;neige;','1999-02-01','1999-02-28','donnees;analyse;','donnees;analyse;','Rousselot, M., Durand, Y., Giraud, G., Merindol, L., and Daniel, L.: Analysis and forecast of extreme new-snow avalanches: a numerical study of the avalanche cycles of February 1999 in France, J. Glaciol., 56, 758–770, 2010.','Rousselot, M., Durand, Y., Giraud, G., Merindol, L., and Daniel, L.: Analysis and forecast of extreme new-snow avalanches: a numerical study\r\nof the avalanche cycles of February 1999 in France, J. Glaciol., 56, 758–770, 2010.','','','2013-01-07');
