INSERT INTO prog VALUES (235,NULL,21,NULL,NULL,NULL,NULL,NULL,NULL,'PIT EMB','Observatoire du Mont-Blanc','','Observation, à l''aide d''indicateurs, de l''évolution de l''état du territoire de l''Espace Mont Blanc. Mise en place des recommandations, des stratégies, des plans d''actions et des mesures du Schéma du Développement Durable.\r\n\r\nLes indicateurs sont répartit en 3 classes :\r\n- Indicateurs environnementaux\r\nBilan de masse des glaciers\r\nQualité de l''air\r\nProduction de déchets\r\nPrélévements en eau\r\nQualité de l''eau des principaux linéaires\r\nNombre et superficie des milieux naturels \r\n\r\n- Indicateurs sociaux\r\n    nombre d''habitants\r\n    taux de natalité\r\n    taux d''excédent naturel\r\n    taux d''accroissement migratoire\r\n    indice de vieillissement de la population\r\n    densité de la population\r\n    nombre d''écoles, de classes et d''élèves\r\n    nombre de professionnels de la santé\r\n    taux de participation aux élections communales\r\n    nombre de logements sociaux par commune\r\n    fréquentation lignes publiques \r\n\r\n- Indicateurs économiques\r\nnombre de permis de construire\r\nnombre d''emplois\r\nnombre de lits hôteliers\r\nnombre de nuitées en hôtel\r\ntrafic dans les tunnels\r\ntaux de chômage\r\ntaux de résidences principales dans le parc immobilier\r\ntaux de résidences secondaires sur le parc immobilier\r\nsuperficie agricole utilisée\r\nsuperficie couverte par la fôret\r\nnombre de commerces de petite surface\r\nnombre de commerces de moyenne surface\r\nnombre de commerces de grande surface\r\nfréquentations des sites touristiques\r\nnombre de journées skieurs par station d''hiver','','RAVA (Région autonome Vallée d''Aoste), Fondation Montagne Sûre,  ARPA (Agence régionale pour la protection de l''environnement ), SIVOM Pays du Mont-Blanc, SED74, Canton du Valais , FDDM (Fondation pour le développement durable des régions de montagne)','glacio;;ecolo;climato','flore;climat;faune;air;eau;glace;','2010-01-25','2013-03-25','cartes;donnees;analyse;','cartes;donnees;analyse;','','','http://geoemb.preproduzione.invallee.it','','2012-12-14');
