INSERT INTO prog VALUES (240,NULL,132,NULL,NULL,NULL,NULL,NULL,NULL,'','Une carte gravimétrique haute résolution du massif du Mont-Blanc : implications structurales','A high-resolution gravity map of the Mont-Blanc massif: structural consequences','Un levé gravimétrique a été réalisé dans les massifs du Mont-Blanc et des Aiguilles-Rouges pour d’une part, améliorer la couverture gravimétrique préexistante au sein des massifs et, d’autre part, permettre, de préciser la géométrie d’un chevauchement d’échelle crustale.','A gravimetric study has been conducted in the Mont-Blanc and aiguilles rouge ranges in order to complete the gravimetric database and give information on the deep structure of the range','INSU, CNRS, IPGP, université Lyon1, Université des sciences et techniques du Languedoc.',';geol;;','tectonique;',NULL,NULL,'cartes;donnees;analyse;','cartes;donnees;analyse;','Frédéric Masson, Frédérick Gal, et  Philippe-Hervé Leloup. Une carte gravimétrique haute résolution du massif du Mont-Blanc : implications structurales, C. R. Geoscience 334 (2002) 1011–1019','Frédéric Masson, Frédérick Gal, et  Philippe-Hervé Leloup. A high-resolution gravity map of the Mont-Blanc massif: structural consequences, C. R. Geoscience 334 (2002) 1011–1019','','','2012-12-13');
