INSERT INTO prog VALUES (266,NULL,138,42,NULL,NULL,NULL,NULL,NULL,'','Etude des potentialités forestières en fonction des peuplements de coléoptères saproxyliques et de syrphidae.','','Le principal objectif de cette étude était de déterminer la diversité biologique de milieux forestiers et leur état de conservation par l’intermédiaire d’un groupe d’insectes indicateurs : coléoptères saproxyliques et diptères Syrphidés.','','DIREN Rhône-Alpes, ASTERS, Univ Toulouse, Syrphys',';;ecolo;','faune;micro-organismes;','2005-01-01','2007-12-31','donnees;analyse;','donnees;analyse;','Brustel, H., Valladares, L., Brin A.,  2007 - Coléoptères saproxyliques d’intérêt patrimonial dans les réserves naturelles des Aiguilles Rouges et des Contamines-Montjoies - échantillonnage de 6 sites forestiers par piégeage. Rapport d’étude ESAP, 32 p.','','','','2013-01-08');
