INSERT INTO prog VALUES (268,NULL,42,NULL,NULL,NULL,NULL,NULL,NULL,'Habitalp','Cartographie des habitats naturels de la réserve des Contamines-Montjoies','','Le survol de la réserve et la prise de photographie infrarouge haute définition ont été faits. L''ensemble du territoire de la réserve a été photo interprété (terminé en 2010) et une partie des habitats, ceux présentant les enjeux principaux, a fait l''objet d''une validation de terrain (2011). Une carte des habitats naturels de la réserve est disponible est fera l''objet d''une valorisation auprès du public','','',';;ecolo;','faune;','2002-08-01',NULL,'cartes;','cartes;','','','http://www.habitalp.de/','','2013-01-10');
